const colors = {
    reset: "\x1b[0m",
    bright: "\x1b[1m",
    dim: "\x1b[2m",
    underscore: "\x1b[4m",
    blink: "\x1b[5m",
    reverse: "\x1b[7m",
    hidden: "\x1b[8m",
    fg: {
        black: "\x1b[30m",
        red: "\x1b[31m",
        green: "\x1b[32m",
        yellow: "\x1b[33m",
        blue: "\x1b[34m",
        magenta: "\x1b[35m",
        cyan: "\x1b[36m",
        white: "\x1b[37m",
        crimson: "\x1b[38m"
    },
    bg: {
        black: "\x1b[40m",
        red: "\x1b[41m",
        green: "\x1b[42m",
        yellow: "\x1b[43m",
        blue: "\x1b[44m",
        magenta: "\x1b[45m",
        cyan: "\x1b[46m",
        white: "\x1b[47m",
        crimson: "\x1b[48m"
    }
};

if(Reflect.getPrototypeOf(console)) {
    console["line"] =  (title) => {
        return console.log(colors.dim, colors.fg.red, `~~~~~~~~~~~~~~~~{ ${title?.replace(/\n/g, '')?.toUpperCase() ?? "N/A"} }~~~~~~~~~~~~~~~~`, colors.reset);
    }
    console["success"] =  (message, title) => {
        $title = title || "Success";
        return console.log(colors.bg.green, $title, colors.reset, message);
    }
    console["error"] =  (message, title) => {
        $title = title || "Error";
        return console.log(colors.bg.red, $title, colors.reset, message);
    }
    console["warn"] =  (message, title) => {
        $title = title || "Warning";
        return console.log(colors.bg.yellow, $title, colors.reset, message);
    }
    console["info"] =  (message, title) => {
        $title = title || "Info";
        return console.log(colors.bg.blue, $title, colors.reset, message);
    }
    console["debug"] =  (message, title) => {
        $title = title || "Debug";
        return console.log(colors.bg.cyan, $title, colors.reset, message);
    }
}